note_models.css:
.card {
  font-family: "Noto Sans", sans-serif;
  font-size: 25px;
  text-align: center;
  color: white;
  background-color: black;
}

.hanzi {
  font-family: "AR PL UKai TW", "Noto Serif CJK TC", serif;
}

.hanzi.big {
  font-size: 75px;
}

.definition {
  font-size: 30px;
  word-wrap: break-word;
}

.answer {
  color: red;
}

/* Experimental CSS, currently not supported */
ruby {
  ruby-position: inter-character;
}

/* Remove replay button from AnkiDroid */
.replaybutton {
  display: none;
}
